import {Pos_ClosingStockReact} from "../../collection/posClosingStock";
import compoLang from '../../../both/i18n/lang/elem-label'

export default {
    data() {
        return {
            newLoading: true,
            closingStockData: [],
            journalClosingStock: [],
            loading: false,
            searchData: '',
            isSearching: false,
            currentPage: 1,
            currentSize: 10,
            count: 0,
            dialogAddClosingStock: false,
            closingStockForm: {
                closeDate: moment().toDate(),
                _id: ""
            },
            rules: {
                closeDate: [{
                    type: 'date',
                    required: true,
                    message: 'Please input Journal Date',
                    trigger: 'blur'
                }],


            },
            options: {
                disabledDate(time) {
                    return false;
                }
            },
            disabledDate: false,
            closeDate: "",
            skip: 0
        }
    },
    watch: {
        currentSize(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * val;
            this.queryData(this.searchData, this.skip, val + this.skip);
        },
        currentPage(val) {
            this.isSearching = true;
            this.skip = (val - 1) * this.currentSize;
            this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
        },
        searchData(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * this.currentSize;
            this.queryData(val, this.skip, this.currentSize + this.skip);
        },
        "closingStockForm.closeDate"(val) {
            let vm = this;
            if (vm.closeDate && vm.closeDate != "" && vm.closeDate != undefined) {
                vm.options = {
                    disabledDate(time) {
                        let min = moment(vm.closeDate).startOf("day").add(1, "days").toDate().getTime();
                        return time.getTime() < min;
                    }
                }
            } else {
                vm.options = {
                    disabledDate(time) {
                        return false;
                    }
                }
            }
        }
    },
    mounted() {
        this.$jQuery('body').off();
        let vm = this;
        vm.options = {
            disabledDate(time) {
                let min = moment(vm.closeDate).startOf("day").add(1, "days").toDate().getTime();
                return time.getTime() < min;
            }
        }
    },
    methods: {
        handleSizeChange(val) {
            this.currentSize = val;
        },
        handleCurrentChange(val) {
            this.currentPage = val;
        },
        queryData: _.debounce(function (val, skip, limit) {
            this.newLoading = true;
            Meteor.call('queryClosingStock', {
                q: val,
                filter: this.filter,
                rolesArea: Session.get('area'),
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {
                    this.closingStockData = result.content;
                    this.count = result.countClosingStock;
                }
                this.isSearching = false;
                this.newLoading = false;
            });
        }, 300),

        popUpAdd() {
            let vm = this;
            Meteor.call("queryLastClosingStock", Session.get("area"), function (err, re) {
                if (re != undefined) {
                    vm.closeDate = re.closeDate;
                } else {
                    vm.closeDate = "";
                }
            })
        },
        saveClosingStock(event) {
            event.preventDefault();

            let vm = this;
            this.$refs["closingStockForm"].validate((valid) => {
                if (valid) {
                    let closingStockDoc = {
                        closeDate: vm.closingStockForm.closeDate,
                        rolesArea: Session.get('area'),
                        day: moment(vm.closingStockForm.closeDate).format("DD"),
                        month: moment(vm.closingStockForm.closeDate).format("MM"),
                        year: moment(vm.closingStockForm.closeDate).format("YYYY"),
                        closeDateName: moment(vm.closingStockForm.closeDate).format("DD/MM/YYYY")
                    };

                    Meteor.call("queryClosingStockByMonthYear", closingStockDoc.day, closingStockDoc.month, closingStockDoc.year, Session.get('area'), function (err, re) {
                        if (re == undefined) {
                            Meteor.call("insertClosingStock", closingStockDoc, (err, result) => {
                                if (!err) {
                                    vm.$message({
                                        duration: 1000,
                                        message: `Save Successfully!`,
                                        type: 'success'
                                    });
                                    vm.dialogAddClosingStock = false;
                                    vm.$refs["closingStockForm"].resetFields();
                                    vm.resetForm();

                                } else {
                                    vm.$message({
                                        duration: 1000,
                                        message: `Duplicate Date!!`,
                                        type: 'error'
                                    });
                                }
                            })
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: `Duplicate Date!!`,
                                type: 'error'
                            });
                        }
                    })

                }
            })

        },

        removeClosingStock(index, row, rows) {
            let vm = this;
            Meteor.call("queryLastClosingStock", Session.get("area"), function (err, re) {
                if (re.month == row.month && re.year == row.year) {
                    vm.$confirm('This will permanently delete the Item. Continue?', 'Warning', {
                        confirmButtonText: 'OK',
                        cancelButtonText: 'Cancel',
                        type: 'warning'
                    }).then(() => {
                        Meteor.call("removeClosingStock", row._id, (err, result) => {
                            if (!err) {
                                rows.splice(index, 1);

                                vm.$message({
                                    message: `លុប ${row.closeDateName} បានជោគជ័យ`,
                                    type: 'success'
                                });
                            } else {
                                vm.$message({
                                    type: 'error',
                                    message: 'Delete Failed'
                                });
                            }

                        })

                    }).catch(() => {
                        vm.$message({
                            type: 'info',
                            message: 'Delete canceled'
                        });
                    });
                } else {
                    vm.$message({
                        type: 'error',
                        message: 'Delete Failed ! Not Last Date!'
                    });
                }
            })


        },
        cancel() {
            this.$message({
                type: 'info',
                message: 'Canceled'
            });
        },
        resetForm() {
            if (this.$refs["closingStockForm"]) {
                this.$refs["closingStockForm"].resetFields();
            }
        }
    },
    created() {
        this.isSearching = true;
        this.queryData();
        Meteor.subscribe('Pos_ClosingStockReact');

    },
    meteor: {
        newRe() {
            let vm = this;
            Pos_ClosingStockReact.find({}).fetch();
            vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
        },
        langSession() {
            return Session.get('lang') || "en";
        },
    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['closingStock'];
            return data;
        }
    }
}
