import PageA4 from '/imports/vue/ui/report/page/PageA4.vue';
import {GenerateFile} from '/imports/api/mixins/file-saver-fn.js';
import compoLangReport from '../../../../both/i18n/lang/elem-label-sch-report';

export default {
    mixins: [GenerateFile],
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            params: {
                branch: '',
                area: '',
                date: [moment().startOf("months").toDate(),moment().endOf("months").toDate()],
                programId: "",
                majorId: "",
                levelId: "",
                promotionId: ""

            },
            rolesArea: '',
            activeName: '1',
            registerHtml: "",
            labelPosition: 'top',
            branchOptions: [],
            areaOptions: [],
            programOptions: [],
            majorOptions: [],
            levelOptions: [],
            promotionOptions: [],


            waterBillingSetup: {
                khName: '',
                enName: ''
            },

            loading: false,
            exportLoading: false,

            checkAll: false,
            isIndeterminate: true,
            dateHeader: "",
            currencyHeader: "",
            pickerDateOptions: {
                shortcuts: [{
                    text: 'Last week',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start= moment().startOf("day").toDate();
                        start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last month',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-1, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last 3 months',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-4, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'This month',
                    onClick(picker) {
                        const end = moment().endOf("month").toDate();
                        const start = moment().startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Today',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start= moment().startOf("day").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }]
            },
            groupByOptions: [
                {label: "None", value: "None"},
                {label: "Transaction Type", value: "Transaction Type"},
                {label: "Customer", value: "Customer"},
                {label: "Item", value: "Item"},
                {label: "Day", value: "Day"},
                {label: "Week", value: "Week"},
                {label: "Month", value: "Month"},
                {label: "Quarter", value: "Quarter"},
                {label: "Year", value: "Year"},
            ]
        };
    },
    meteor: {
        rolesArea() {
            return Session.get('area');
        },
        langSessionReport() {
            return Session.get('lang') || "en";
        }
    },
    watch: {

        "params.branch"(val) {
            this.params.area = "";
            this.fetchArea(val);
        },
        "params.programId"(val) {
            if (val !== "") {
                this.fetchMajor(val);
            } else {
                this.params.majorId = "";
            }
        },
        "params.majorId"(val) {
            if (val !== "") {
                this.fetchLevel(val);
            } else {
                this.params.levelId = "";
            }
        }
    },
    created() {
        Meteor.call('getWaterBillingSetup', Session.get('area'), (err, result) => {
            if (result) {
                this.waterBillingSetup = result;
            }
        })
        this.fetchBranch();
        this.fetchProgram();
        this.fetchPromotion();
    },
    methods: {

        fetchBranch() {
            Meteor.call("queryRoleBranchOptionReport", (err, result) => {
                if (result) {
                    this.branchOptions = result;
                }
            })
        },
        fetchArea(branchList) {
            Meteor.call("queryRoleAreaOptionReport", branchList, (err, result) => {
                if (result) {
                    this.areaOptions = result;
                }
            })
        },
        fetchProgram() {
            let selector = {};
            Meteor.call("queryProgramOption", selector, (err, result) => {
                if (result) {
                    this.programOptions = result;
                }
            })
        },
        fetchMajor(programId) {
            let selector = {};
            selector.programId = programId;
            Meteor.call("queryMajorOption", selector, (err, result) => {
                if (result) {
                    this.majorOptions = result;
                }
            })
        },
        fetchLevel(majorId) {
            let selector = {};
            selector.majorId = majorId;
            Meteor.call("queryLevelOption", selector, (err, result) => {
                if (result) {
                    this.levelOptions = result;
                }
            })
        },
        fetchPromotion() {
            let selector = {};
            Meteor.call("queryPromotionOptionNoStatus", selector, (err, result) => {
                if (result) {
                    this.promotionOptions = result;
                }
            })
        },
        handleRun() {
            this.loading = true;

            if (this.params.date == "" || this.params.date == undefined) {
                alertify.error("Date can't not empty!!");
                this.loading = false;
                return false;
            }
            Meteor.call('schRegisterReport', this.params, this.langConfig, (err, result) => {
                if (result) {
                    this.registerHtml = result.registerHTML;
                    this.dateHeader = result.dateHeader;
                    this.currencyHeader = result.currencyHeader;
                }
                this.loading = false;
            });
        },
        onLoadHandler: function () {
            this.onLoad = true;
        }


    },
    computed: {
        dataExist() {
            // return this.posSaleData.length > 0;
        },
        langConfig() {
            let data = compoLangReport.filter(config => config.lang === this.langSessionReport)[0]['register'];
            return data;
        }
    },
    components: {
        a4: PageA4
    },
}
