import compoLang from '../../../both/i18n/lang/elem-label-loan'
import {Loan_EscapeDayReact} from "../../collection/loanEscapeDay";

export default {
    meteor: {
        langSession() {
            return Session.get('lang') || "en";
        },
        disabledRemove() {
            return Session.get("canRemove");
        },
        disabledUpdate() {
            return Session.get("canUpdate");
        },
        newRe() {
            let vm = this;
            Loan_EscapeDayReact.find({}).fetch();
            vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
        }
    },
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            newLoading: true,
            langSession: null,
            loanEscapeDayData: [],
            loading: false,
            searchData: '',
            isSearching: false,
            currentPage: 1,
            currentSize: 10,
            count: 0,
            dialogAddLoanEscapeDay: false,
            dialogUpdateLoanEscapeDay: false,

            loanEscapeDayForm: {
                year: "",
                dateList: [],
                description: "",
                _id: ""
            },
            rules: {
                year: [{
                    type: 'date',
                    required: true,
                    message: 'Please input Year',
                    trigger: 'blur'
                }],
            },
            typeOption: [
                {label: "Amount", value: "A"},
                {label: "Percent", value: "P"}
            ],
            termDataOption: [],
            skip: 0
        }
    },
    watch: {
        currentSize(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * val;
            this.queryData(this.searchData, this.skip, val + this.skip);
        },
        currentPage(val) {
            this.isSearching = true;
            this.skip = (val - 1) * this.currentSize;
            this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
        },
        searchData(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * this.currentSize;
            this.queryData(val, this.skip, this.currentSize + this.skip);
        }
    },
    methods: {
        handleSizeChange(val) {
            this.currentSize = val;
        },
        handleCurrentChange(val) {
            this.currentPage = val;
        },
        queryData: _.debounce(function (val, skip, limit) {
            this.newLoading = true;
            Meteor.call('queryLoanEscapeDay', {
                q: val,
                filter: this.filter,
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {

                    if (!err) {
                        this.loanEscapeDayData = result.content.map((obj) => {
                            obj.dateListName = JSON.stringify(obj.dateListName);
                            return obj;
                        });
                        this.count = result.countExchange;
                    }

                    this.count = result.countLoanEscapeDay;
                }
                this.isSearching = false;
                this.newLoading = false;
            });
        }, 300),
        saveLoanEscapeDay(event) {
            event.preventDefault();
            let vm = this;
            this.$refs["loanEscapeDayFormAdd"].validate((valid) => {
                if (valid) {

                    let dateList = [];
                    vm.loanEscapeDayForm.dateList.forEach((obj) => {
                        dateList.push(moment(obj).startOf("day").add(12, "hour").toDate())
                    });
                    let loanEscapeDayDoc = {
                        year: moment(vm.loanEscapeDayForm.year).startOf("day").add(12, "hour").toDate(),
                        dateList: dateList,
                        description: vm.loanEscapeDayForm.description,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("insertLoanEscapeDay", loanEscapeDayDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['saveSuccess'],
                                type: 'success'
                            });
                            vm.dialogAddLoanEscapeDay = false;
                            vm.$refs["loanEscapeDayFormAdd"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: err.message,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        updateLoanEscapeDay() {
            let vm = this;
            this.$refs["loanEscapeDayFormUpdate"].validate((valid) => {
                if (valid) {
                    let dateList = [];
                    vm.loanEscapeDayForm.dateList.forEach((obj) => {
                        dateList.push(moment(obj).startOf("day").add(12, "hour").toDate())
                    });
                    let loanEscapeDayDoc = {
                        _id: vm.loanEscapeDayForm._id,
                        year: moment(vm.loanEscapeDayForm.year).startOf("day").add(12, "hour").toDate(),
                        dateList: dateList,
                        description: vm.loanEscapeDayForm.description,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("updateLoanEscapeDay", loanEscapeDayDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateSuccess'],
                                type: 'success'
                            });
                            vm.dialogUpdateLoanEscapeDay = false;

                            vm.$refs["loanEscapeDayFormUpdate"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateFail'],
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        removeLoanEscapeDay(index, row, rows) {
            let vm = this;
            this.$confirm(this.langConfig['message'], this.langConfig['warning'], {
                confirmButtonText: 'OK',
                cancelButtonText: 'Cancel',
                type: 'warning'
            }).then(() => {
                Meteor.call("removeLoanEscapeDay", row._id, (err, result) => {
                    if (!err) {
                        rows.splice(index, 1);

                        vm.$message({
                            message: `${row.year}` + this.langConfig['removeSuccess'],
                            type: 'success'
                        });


                    } else {
                        vm.$message({
                            type: 'error',
                            message: this.langConfig['removeFail']
                        });
                    }

                })

            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: this.langConfig['cancel']
                });
            });
        },
        findLoanEscapeDayById(doc) {
            let vm = this;
            vm.loanEscapeDayForm = {};

            Meteor.call("queryLoanEscapeDayById", doc.row._id, (err, result) => {
                if (result) {
                    vm.loanEscapeDayForm._id = result._id;
                    vm.loanEscapeDayForm = result;
                }
            })
        },
        cancel() {
            this.$message({
                type: 'info',
                message: this.langConfig['cancel']
            });
        },
        resetForm() {
            this.loanEscapeDayForm._id = "";
            if (this.$refs["loanEscapeDayFormAdd"]) {
                this.$refs["loanEscapeDayFormAdd"].resetFields();
            }

            if (this.$refs["loanEscapeDayFormUpdate"]) {
                this.$refs["loanEscapeDayFormUpdate"].resetFields();
            }

        }
    },
    created() {
        this.isSearching = true;
        this.queryData();
        Meteor.subscribe('Loan_EscapeDayReact');

    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['escapeDay'];
            return data;
        }
    }
}
