import PageA4 from '/imports/vue/ui/report/page/PageA4.vue';
import {GenerateFile} from '/imports/api/mixins/file-saver-fn.js';

export default {
    mixins: [GenerateFile],
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            params: {
                branch: '',
                area: '',
                currency: '',
                date: moment().toDate(),
            },
            rolesArea: '',
            activeName: '1',
            fixedAssetsData: [],
            fixedAssetHtml: "",
            branchOptions: [],
            areaOptions: [],
            exchangeOptions: [],
            currencyOptions: [
                {label: "KHR", value: "KHR"},
                {label: "USD", value: "USD"},
                {label: "THB", value: "THB"},
            ],

            waterBillingSetup: {
                khName: '',
                enName: ''
            },

            loading: false,
            exportLoading: false,
            pickerDateOptions: {
                shortcuts: [{
                    text: 'Last week',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start = moment().startOf("day").toDate();
                        start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last month',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-1, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last 3 months',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-4, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'This month',
                    onClick(picker) {
                        const end = moment().endOf("month").toDate();
                        const start = moment().startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Today',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start = moment().startOf("day").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }]
            },
            dateHeader: ""
        };
    },
    meteor: {
        rolesArea() {
            return Session.get('area');
        }
    },
    watch: {
        "params.branch"(val) {
            this.params.area = "";
            this.fetchArea(val);
        }
    },
    created() {
        Meteor.call('getWaterBillingSetup', Session.get('area'), (err, result) => {
            if (result) {
                this.waterBillingSetup = result;
            }
        })
        this.fetchBranch();
        this.fetchExchange();
    },
    methods: {

        fetchBranch() {
            Meteor.call("queryRoleBranchOptionReport", (err, result) => {
                if (result) {
                    this.branchOptions = result;
                }
            })
        },
        fetchArea(branchList) {
            Meteor.call("queryRoleAreaOptionReport", branchList, (err, result) => {
                if (result) {
                    this.areaOptions = result;
                }
            })
        },
        handleRun() {
            this.loading = true;

            if (this.params.date == "" || this.params.date == undefined) {
                alertify.error("Date can't not empty!!");
                this.loading = false;
                return false;
            }

            Meteor.call('fixedAssetReport', this.params, (err, result) => {
                if (result) {
                    this.fixedAssetsData = result.fixedAssetList;
                    this.fixedAssetHtml = result.fixedAssetHtml;
                    this.dateHeader = result.dateHeader;
                }
                this.loading = false;
            });
        }, fetchExchange() {
            Meteor.call('queryExchangeOptionReport', (err, result) => {
                if (result) {
                    this.exchangeOptions = result;
                }
            });
        }
    },
    computed: {
        dataExist() {
            return this.fixedAssetsData.length > 0;
        }
    },
    components: {
        a4: PageA4
    },
}
