import PageA4 from '/imports/vue/ui/report/page/PageA4.vue';
import {GenerateFile} from '/imports/api/mixins/file-saver-fn.js';

export default {
    mixins: [GenerateFile],
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            params: {
                branch: '',
                area: '',
                currency: '',
                checkedAccountType: [],
                account: "",
                date: [moment().startOf("months").toDate(),moment().endOf("months").toDate()],
                exchangeId: ""
            },
            rolesArea: '',
            activeName: '1',
            ledgerHtml: "",
            labelPosition: 'top',
            branchOptions: [],
            areaOptions: [],
            exchangeOptions: [],
            currencyOptions: [
                {label: "KHR", value: "KHR"},
                {label: "USD", value: "USD"},
                {label: "THB", value: "THB"},
            ],
            accountOptions: [],

            waterBillingSetup: {
                khName: '',
                enName: ''
            },

            loading: false,
            exportLoading: false,

            checkAll: false,
            accountTypeOptions: [],
            accountTypes: [],
            isIndeterminate: true,
            dateHeader: "",
            currencyHeader: "",
            pickerOptions: {
                shortcuts: [{
                    text: 'Last week',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start= moment().startOf("day").toDate();
                        start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last month',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-1, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last 3 months',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-4, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'This month',
                    onClick(picker) {
                        const end = moment().endOf("month").toDate();
                        const start = moment().startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Today',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start= moment().startOf("day").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }]
            },
        };
    },
    meteor: {
        rolesArea() {
            return Session.get('area');
        }
    },
    watch: {

        "params.branch"(val) {
            this.params.area = "";
            this.fetchArea(val);
        },
        "params.checkedAccountType"(val) {
            this.fetchAccount(val);
        },
        "ledgerHtml"(val) {
            if (val) {
                this.$nextTick(() => {
                    //Do jQuery stuff here
                    $(".rowClick").dblclick(function (e, t) {
                        let params = {};
                        let queryParams = {};
                        queryParams._id = e.currentTarget.id;
                        queryParams.area = Session.get("area");
                        queryParams.module = Session.get("module");

                        let path = FlowRouter.path("acc.journalFilter", params, queryParams);
                        window.open(path, "_blank");
                    });
                })
            }
        }
    },
    created() {
        Meteor.call('getWaterBillingSetup', Session.get('area'), (err, result) => {
            if (result) {
                this.waterBillingSetup = result;
            }
        })
        this.fetchAccountType();
        this.fetchExchange();
        this.fetchBranch();


        let q = FlowRouter.current().queryParams;
        if (q && q.account) {
            let arr = [];
            arr.push(q.checkedAccountType);
            this.params.branch = q.selectBranch;
            this.params.area = q.selectArea;
            this.params.currency = q.currency;
            this.params.checkedAccountType = arr;
            this.params.date = q.date;
            this.params.exchangeId = q.exchangeId;
            this.params.account = q.account;
            this.handleRun();
        }

    },
    methods: {

        fetchAccountType() {
            Meteor.call('queryAccountTypeOptionReport', (err, result) => {
                if (result) {
                    this.accountTypeOptions = result.listWithId;
                    this.accountTypes = result.list;
                }
            });
        },
        fetchExchange() {
            Meteor.call('queryExchangeOptionReport', (err, result) => {
                if (result) {
                    this.exchangeOptions = result;
                }
            });
        },
        fetchAccount(accountTypeList) {
            let selector = {};
            selector.accountTypeId = {$in: accountTypeList}
            Meteor.call('queryChartAccountOptionReport', selector, (err, result) => {
                if (result) {
                    this.accountOptions = result;
                }
            });
        },
        fetchBranch() {
            Meteor.call("queryRoleBranchOptionReport", (err, result) => {
                if (result) {
                    this.branchOptions = result;
                }
            })
        },
        fetchArea(branchList) {
            Meteor.call("queryRoleAreaOptionReport", branchList, (err, result) => {
                if (result) {
                    this.areaOptions = result;
                }
            })
        },
        handleRun() {
            this.loading = true;

            if (this.params.date == "" || this.params.date == undefined) {
                alertify.error("Date can't not empty!!");
                this.loading = false;
                return false;
            }
            if (this.params.exchangeId == "" || this.params.exchangeId == undefined) {
                alertify.error("Exchange can't not empty!!");
                this.loading = false;
                return false;
            }

            Meteor.call('transactionDetailReport', this.params, (err, result) => {
                if (result) {
                    this.ledgerHtml = result.ledgerHTML;
                    this.dateHeader = result.dateHeader;
                    this.currencyHeader = result.currencyHeader;
                }
                this.loading = false;
            });
        },
        handleCheckAllChange() {
            this.params.checkedAccountType = this.checkAll ? this.accountTypeOptions : [];
            this.isIndeterminate = false;
        },
        handleCheckedAccountTypeChange(value) {
            let checkedCount = value.length;
            this.checkAll = checkedCount === this.accountTypes.length;
            this.isIndeterminate = checkedCount > 0 && checkedCount < this.accountTypes.length;
        },


    },
    computed: {
        dataExist() {
            return this.journalsData.length > 0;
        }
    },
    components: {
        a4: PageA4
    },
}
