import PageA4 from '/imports/vue/ui/report/page/PageA4.vue';
import {GenerateFile} from '/imports/api/mixins/file-saver-fn.js';

export default {
    mixins: [GenerateFile],
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            params: {
                branch: '',
                area: '',
                currency: '',
                date: moment().toDate(),
                exchangeId: ""
            },
            rolesArea: '',
            activeName: '1',
            trialBalanceHtml: "",
            labelPosition: 'top',
            branchOptions: [],
            areaOptions: [],
            exchangeOptions: [],
            currencyOptions: [
                {label: "KHR", value: "KHR"},
                {label: "USD", value: "USD"},
                {label: "THB", value: "THB"},
            ],

            waterBillingSetup: {
                khName: '',
                enName: ''
            },

            loading: false,
            exportLoading: false,

            dateHeader: "",
            currencyHeader: ""
        };
    },
    meteor: {
        rolesArea() {
            return Session.get('area');
        }
    },
    watch: {

        "params.branch"(val) {
            this.params.area = "";
            this.fetchArea(val);
        },
        "trialBalanceHtml"(val) {
            let vm=this;
            if (val) {
                this.$nextTick(() => {
                    //Do jQuery stuff here
                    $(".rowClick").dblclick(function (e, t) {
                        let params = {};
                        let queryParams = {};
                        queryParams.account = e.currentTarget.id;
                        queryParams.area = Session.get("area");
                        queryParams.module = Session.get("module");

                        queryParams.selectBranch = vm.params.branch;
                        queryParams.selectArea = vm.params.area;
                        queryParams.currency = vm.params.currency;
                        let dateList = [];
                        dateList.push(moment(vm.params.date).startOf("months").toDate());
                        dateList.push(moment(vm.params.date).toDate());

                        queryParams.date = dateList;
                        queryParams.exchangeId = vm.params.exchangeId;

                        Meteor.call("queryChartAccountById", e.currentTarget.id, (err, result) => {
                            queryParams.checkedAccountType = result.accountTypeId;
                            let path = FlowRouter.path("acc.ledgerReportNewPage", params, queryParams);
                            window.open(path, "_blank");
                        })

                    });
                })
            }
        }
    },
    created() {
        Meteor.call('getWaterBillingSetup', Session.get('area'), (err, result) => {
            if (result) {
                this.waterBillingSetup = result;
            }
        })
        this.fetchExchange();
        this.fetchBranch();
    },
    methods: {

        fetchExchange() {
            Meteor.call('queryExchangeOptionReport', (err, result) => {
                if (result) {
                    this.exchangeOptions = result;
                }
            });
        },

        fetchBranch() {
            Meteor.call("queryRoleBranchOptionReport", (err, result) => {
                if (result) {
                    this.branchOptions = result;
                }
            })
        },
        fetchArea(branchList) {
            Meteor.call("queryRoleAreaOptionReport", branchList, (err, result) => {
                if (result) {
                    this.areaOptions = result;
                }
            })
        },
        handleRun() {
            this.loading = true;

            if (this.params.date == "" || this.params.date == undefined) {
                alertify.error("Date can't not empty!!");
                this.loading = false;
                return false;
            }
            if (this.params.exchangeId == "" || this.params.exchangeId == undefined) {
                alertify.error("Exchange can't not empty!!");
                this.loading = false;
                return false;
            }

            Meteor.call('trialBalanceReport', this.params, (err, result) => {
                if (result) {
                    this.trialBalanceHtml = result.trialBalanceHTML;
                    this.dateHeader = result.dateHeader;
                    this.currencyHeader = result.currencyHeader;
                }
                this.loading = false;
            });
        }

    },
    computed: {
        dataExist() {
            return this.journalsData.length > 0;
        }
    },
    components: {
        a4: PageA4
    },
}
