                    duration: 1000,
                    message: this.langConfig['expiredDateForAction'],
                    type: 'error'
                });
                return false;
            }

            //Check with closing stock
            if (vm.closingStockDate && vm.closingStockDate !== "" && moment(vm.closingStockDate).endOf("day").toDate().getTime() >= row.date.getTime()) {
                vm.$message({
                    duration: 1000,
                    message: this.langConfig['expiredDateForAction'],
                    type: 'error'
                });
                return false;
            }

            //Check with closing stock
            if (vm.closeDate && vm.closeDate !== "" && moment(vm.closeDate).endOf("day").toDate().getTime() >= row.date.getTime()) {
                vm.$message({
                    duration: 1000,
                    message: this.langConfig['expiredDateForAction'],
                    type: 'error'
                });
                return false;
            }

            this.$confirm('This will permanently delete the Item. Continue?', 'Warning', {
                confirmButtonText: 'OK',
                cancelButtonText: 'Cancel',
                type: 'warning'
            }).then(() => {
                Meteor.call("removePosConvertInventory", row._id, (err, result) => {
                    if (!err) {
                        rows.splice(index, 1);

                        vm.$message({
                            message: `
                     ${row.code} : ${row.productId} `,
                            type: 'success'
                        });

                    } else {
                        vm.$message({
                            type: 'error',
                            message: 'Delete Fialed'
                        });
                    }

                })

            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: 'Delete canceled'
                });
            });


        },
        popUpConvertInventoryAdd() {
            let vm = this;
            if (vm.closingStockDate !== "") {
                if (vm.closeDate && vm.closeDate !== "" && vm.closingStockDate.getTime() < vm.closeDate.getTime()) {
                    vm.options = {
                        disabledDate(time) {
                            let min = moment(vm.closeDate).add(1, "days").toDate().getTime();
                            return time.getTime() < min;
                        }
                    };
                    vm.posConvertInventoryForm.date = moment(vm.closeDate).add(1, "days").toDate();
                } else {
                    vm.options = {
                        disabledDate(time) {
                            let min = moment(vm.closingStockDate).startOf("day").add(1, "days").toDate().getTime();
                            return time.getTime() < min;
                        }
                    };
                    vm.posConvertInventoryForm.date = moment(vm.closingStockDate).add(1, "days").toDate();
                }
            }
        },
        popUpConvertInventoryUpdate(row) {
            let vm = this;
            if (vm.isDetectedDate === true && moment().startOf("day").toDate().getTime() >= row.date.getTime()) {
                vm.$message({
                    duration: 1000,
                    message: this.langConfig['expiredDateForAction'],
                    type: 'error'
                });
                return false;
            }


            //Check with closing stock
            if (vm.closingStockDate && vm.closingStockDate !== "" && moment(vm.closingStockDate).endOf("day").toDate().getTime() >= row.date.getTime()) {
                vm.$message({
                    duration: 1000,
                    message: this.langConfig['expiredDateForAction'],
                    type: 'error'
                });
                return false;
            }

            //Check with closing stock
            if (vm.closeDate && vm.closeDate !== "" && moment(vm.closeDate).endOf("day").toDate().getTime() >= row.date.getTime()) {
                vm.$message({
                    duration: 1000,
                    message: this.langConfig['expiredDateForAction'],
                    type: 'error'
                });
                return false;
            }

            vm.dialogUpdatePosConvertInventory = true;
        },
        findPosConvertInventoryById(doc) {
            let vm = this;
            vm.posConvertInventoryForm = {};

            Meteor.call("queryPosConvertInventoryById", doc.row._id, (err, result) => {
                if (result) {
                    vm.posConvertInventoryForm._id = result._id;
                    vm.posConvertInventoryForm = result;
                    vm.convertData = result.convert || [{
                        productId: "",
                        qty: 0
                    }];
                }
            })
        },

        cancel() {
            this.$message({
                type: 'info',
                message: 'Canceled'
            });
        },
        resetForm() {
            this.posConvertInventoryForm._id = "";

            this.convertData = [{
                productId: "",
                qty: 0
            }];
            if (this.$refs["posConvertInventoryFormAdd"]) {
                this.$refs["posConvertInventoryFormAdd"].resetFields();
            }

            if (this.$refs["posConvertInventoryFormUpdate"]) {
                this.$refs["posConvertInventoryFormUpdate"].resetFields();
            }

        },
        handleAddConvert() {
            this.convertData.push({
                productId: "",
                qty: 0
            })
        },
        handleEditConvert(index, row) {
            this.convertData[index] = row;
        },
        removeConvertData(index, row) {
            if (this.convertData.length > 1) {
                this.convertData.splice(index, 1);
                this.$message({
                    message: ` ${row.productId} `,
                    type: 'success'
                });
            } else {
                this.convertData = [{
                    productId: "",
                    qty: 0
                }];
            }
        }
    },
    created() {
        let vm = this;
        this.isSearching = true;
        this.productOpt();
        this.locationOpt();
        this.queryData();
        Meteor.subscribe('Pos_ConvertInventoryReact');
        let companyDoc = WB_waterBillingSetup.findOne({});
        this.isDetectedDate = companyDoc.isDetectedDate;
        //Get Last Closing Stock
        Meteor.call("queryLastClosingStock", Session.get('area'), (err, result) => {
            if (result) {
                this.closingStockDate = result.closeDate;
            } else {
                this.closingStockDate = "";
            }
        })
        //Get Last CLosing entry from Account
        if (companyDoc.integratedPosAccount) {
            Meteor.call("queryLastClosingEntry", Session.get("area"), function (err, re) {
                if (re !== undefined) {
                    vm.closeDate = re.closeDate;
                } else {
                    vm.closeDate = "";
                }
            })
        }
    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['convertInventory'];
            return data;
        }
    }
}
