import PageA4 from '/imports/vue/ui/report/page/PageA4.vue';
import {GenerateFile} from '/imports/api/mixins/file-saver-fn.js';
import compoLangReport from '../../../../both/i18n/lang/elem-label-report';

export default {
    mixins: [GenerateFile],
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            params: {
                branch: '',
                area: '',
                date: [moment().startOf("months").toDate(),moment().endOf("months").toDate()],
                locationId: "",
                categoryId: "",
                productId: "",
                customerId:""

            },
            rolesArea: '',
            activeName: '1',
            saleHtml: "",
            labelPosition: 'top',
            branchOptions: [],
            areaOptions: [],
            categoryOptions: [],
            productOptions: [],
            locationOptions: [],
            customerOption: [],

            waterBillingSetup: {
                khName: '',
                enName: ''
            },

            loading: false,
            exportLoading: false,

            checkAll: false,
            isIndeterminate: true,
            dateHeader: "",
            currencyHeader: "",
            pickerDateOptions: {
                shortcuts: [{
                    text: 'Last week',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start = moment().startOf("day").toDate();
                        start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last month',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-1, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Last 3 months',
                    onClick(picker) {
                        const end = moment().add(-1, "month").endOf("month").toDate();
                        const start = moment().add(-4, "month").startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'This month',
                    onClick(picker) {
                        const end = moment().endOf("month").toDate();
                        const start = moment().startOf("month").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }, {
                    text: 'Today',
                    onClick(picker) {
                        const end = moment().endOf("day").toDate();
                        const start = moment().startOf("day").toDate();
                        picker.$emit('pick', [start, end]);
                    }
                }]
            },
        };
    },
    meteor: {
        rolesArea() {
            return Session.get('area');
        },
        langSessionReport() {
            return Session.get('lang') || "en";
        }
    },
    watch: {

        "params.branch"(val) {
            this.params.area = "";
            this.fetchArea(val);
        },
        "params.categoryId"(val) {
            this.params.categoryId = val;
            this.params.productId = "";
            this.productOpt("");
        }
    },
    created() {
        Meteor.call('getWaterBillingSetup', Session.get('area'), (err, result) => {
            if (result) {
                this.waterBillingSetup = result;
            }
        })
        this.fetchBranch();
        this.fetchLocation();
        this.categoryOpt();
        this.customerOpt();
    },
    methods: {

        fetchBranch() {
            Meteor.call("queryRoleBranchOptionReport", (err, result) => {
                if (result) {
                    this.branchOptions = result;
                }
            })
        },
        fetchArea(branchList) {
            Meteor.call("queryRoleAreaOptionReport", branchList, (err, result) => {
                if (result) {
                    this.areaOptions = result;
                }
            })
        },
        fetchLocation(branchList) {
            Meteor.call("queryLocationOptionReport", Session.get('area'), (err, result) => {
                if (result) {
                    this.locationOptions = result;
                }
            })
        },
        productOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    Meteor.call('queryItemOptionReport', query, this.params.categoryId, (err, result) => {
                        if (!err) {
                            this.productOptions = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryItemOptionReport', "", this.params.categoryId, (err, result) => {
                    if (!err) {
                        this.productOptions = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        customerOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    let lists = [];
                    Meteor.call('queryPosCustomerOption', query, Session.get("area"), (err, result) => {
                        if (!err) {
                            this.customerOption = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryPosCustomerOption', "", Session.get("area"), (err, result) => {
                    if (!err) {
                        this.customerOption = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        categoryOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    Meteor.call('queryCategoryOptionReport', query, (err, result) => {
                        if (!err) {
                            this.categoryOptions = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryCategoryOptionReport', "", (err, result) => {
                    if (!err) {
                        this.categoryOptions = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        handleRun() {
            this.loading = true;

            if (this.params.date == "" || this.params.date == undefined) {
                alertify.error("Date can't not empty!!");
                this.loading = false;
                return false;
            }
            Meteor.call('posSaleByProductSummaryReport', this.params, this.langConfig, (err, result) => {
                if (result) {
                    this.saleHtml = result.saleHTML;
                    this.dateHeader = result.dateHeader;
                    this.currencyHeader = result.currencyHeader;
                }
                this.loading = false;
            });
        }


    },
    computed: {
        dataExist() {
            // return this.posSaleData.length > 0;
        },
        langConfig() {
            let data = compoLangReport.filter(config => config.lang === this.langSessionReport)[0]['saleByProductSummary'];
            return data;
        }
    },
    components: {
        a4: PageA4
    },
}
