import compoLang from '../../../both/i18n/lang/elem-label-sch'
import {Sch_ProgramReact} from "../../collection/schProgram";

export default {
    meteor: {
        langSession() {
            return Session.get('lang') || "en";
        },
        disabledRemove() {
            return Session.get("canRemove");
        },
        disabledUpdate() {
            return Session.get("canUpdate");
        },
        newRe() {
            let vm = this;
            Sch_ProgramReact.find({}).fetch();
            vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
        }
    },
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            newLoading: true,
            schProgramData: [],
            loading: false,
            searchData: '',
            isSearching: false,
            currentPage: 1,
            currentSize: 10,
            count: 0,
            dialogAddSchProgram: false,
            dialogUpdateSchProgram: false,

            schProgramForm: {
                name: "",
                khName: "",
                code: "",
                desc: "",
                _id: ""
            },
            rules: {
                name: [{required: true, message: 'Please input name', trigger: 'blur'}],
                code: [{required: true, message: 'Please input code', trigger: 'blur'}]
            },
            skip: 0
        }
    },
    watch: {
        currentSize(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * val;
            this.queryData(this.searchData, this.skip, val + this.skip);
        },
        currentPage(val) {
            this.isSearching = true;
            this.skip = (val - 1) * this.currentSize;
            this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
        },
        searchData(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * this.currentSize;
            this.queryData(val, this.skip, this.currentSize + this.skip);
        }
    },
    methods: {
        handleSizeChange(val) {
            this.currentSize = val;
        },
        handleCurrentChange(val) {
            this.currentPage = val;
        },
        queryData: _.debounce(function (val, skip, limit) {
            this.newLoading = true;
            Meteor.call('querySchProgram', {
                q: val,
                filter: this.filter,
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {
                    this.schProgramData = result.content;
                    this.count = result.countSchProgram;
                }
                this.isSearching = false;
                this.newLoading = false;
            });
        }, 300),

        fetchUser() {
            Meteor.call("queryUserOption", (err, result) => {
                this.applyUserOption = result;
            })
        },
        saveSchProgram(event) {
            event.preventDefault();

            let vm = this;
            this.$refs["schProgramFormAdd"].validate((valid) => {
                if (valid) {
                    let schProgramDoc = {
                        code: vm.schProgramForm.code,
                        name: vm.schProgramForm.name,
                        khName: vm.schProgramForm.khName,
                        desc: vm.schProgramForm.desc,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("insertSchProgram", schProgramDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: `Save Successfully!`,
                                type: 'success'
                            });
                            vm.dialogAddSchProgram = false;

                            vm.$refs["schProgramFormAdd"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: err.message,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        updateSchProgram() {
            let vm = this;
            this.$refs["schProgramFormUpdate"].validate((valid) => {
                if (valid) {
                    let schProgramDoc = {
                        _id: vm.schProgramForm._id,
                        code: vm.schProgramForm.code,
                        name: vm.schProgramForm.name,
                        khName: vm.schProgramForm.khName,
                        desc: vm.schProgramForm.desc,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("updateSchProgram", schProgramDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: `
                    Update
                    Successfully
                    !`,
                                type: 'success'
                            });
                            vm.dialogUpdateSchProgram = false;

                            vm.$refs["schProgramFormUpdate"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: `
                    Update
                    Failed
                    !`,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        removeSchProgram(index, row, rows) {
            let vm = this;
            this.$confirm('This will permanently delete the Item. Continue?', 'Warning', {
                confirmButtonText: 'OK',
                cancelButtonText: 'Cancel',
                type: 'warning'
            }).then(() => {
                Meteor.call("removeSchProgram", row._id, (err, result) => {
                    if (!err) {
                        rows.splice(index, 1);

                        vm.$message({
                            message: `
                    លុប ${row.code} : ${row.name} បានជោគជ័យ`,
                            type: 'success'
                        });


                    } else {
                        vm.$message({
                            type: 'error',
                            message: 'Delete Fialed'
                        });
                    }

                })

            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: 'Delete canceled'
                });
            });


        },
        findSchProgramById(doc) {
            let vm = this;
            vm.schProgramForm = {};
            Meteor.call("querySchProgramById", doc.row._id, (err, result) => {
                if (result) {
                    vm.schProgramForm._id = result._id;
                    vm.schProgramForm = result;
                }
            })
        },
        cancel() {
            this.$message({
                type: 'info',
                message: 'Canceled'
            });
        },
        resetForm() {
            this.schProgramForm._id = "";
            if (this.$refs["schProgramFormAdd"]) {
                this.$refs["schProgramFormAdd"].resetFields();
            }

            if (this.$refs["schProgramFormUpdate"]) {
                this.$refs["schProgramFormUpdate"].resetFields();
            }

        }
    },
    created() {
        this.isSearching = true;
        this.fetchUser();
        this.queryData();
        Meteor.subscribe('Sch_ProgramReact');

    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['program'];
            return data;
        }
    }
}
