import PageA4 from '/imports/vue/ui/report/page/PageA4.vue';
import {GenerateFile} from '/imports/api/mixins/file-saver-fn.js';
import compoLangReport from '../../../../both/i18n/lang/elem-label-loan-report';
import {WB_waterBillingSetup} from '../../../collection/waterBillingSetup'


export default {
    mixins: [GenerateFile],
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            params: {
                branch: '',
                area: '',
                disbursementId: ""
            },
            rolesArea: '',
            activeName: '1',
            repaymentScheduleHtml: "",
            labelPosition: 'top',
            branchOptions: [],
            areaOptions: [],
            clientOptions: [],
            isOperationFee: false,
            isHasBankAccount: false,
            isNoteUp: false,
            isNoteDown: true,
            NotHideRate: true,
            hideRate: false,
            currencySymbol: "",
            waterBillingSetup: {
                khName: '',
                enName: ''
            },
            isFooter: false,
            loading: false,
            exportLoading: false,

            checkAll: false,
            isIndeterminate: true,
            dateHeader: "",
            currencyHeader: "",
            rules: {
                disbursementId: [{required: true, message: 'Please input Disbursement', trigger: 'change'}],
            },
            disbursementDoc: {},
            clientDoc: {},
            productDoc: {},
            creditOfficerDoc: {},
            bankAccount: ""
        };
    },
    meteor: {
        rolesArea() {
            return Session.get('area');
        },
        langSessionReport() {
            return Session.get('lang') || "en";
        }
    },
    watch: {

        "params.branch"(val) {
            this.params.area = "";
            this.fetchArea(val);
        }
    },
    created() {
        Meteor.call('getWaterBillingSetup', Session.get('area'), (err, result) => {
            if (result) {
                this.waterBillingSetup = result;
                if (this.waterBillingSetup.note != "") {
                    this.isFooter = true;
                }
                this.isFooter = this.waterBillingSetup && this.waterBillingSetup.note && this.waterBillingSetup.note != "" ? true : false;
            }
        })
        this.fetchBranch();
        this.clientOpt();


        let setupDoc = WB_waterBillingSetup.findOne({});
        this.NotHideRate = !(setupDoc.isHideRate || false);
        this.hideRate = setupDoc.isHideRate || false;
        this.isOperationFee = setupDoc.isOperationFee;
        this.isHasBankAccount = setupDoc.bankAccount && setupDoc.bankAccount != "" ? true : false;

        this.isNoteUp = setupDoc.isNoteUp || false;
        this.isNoteDown = !(setupDoc.isNoteUp || false);
        this.bankAccount = setupDoc.bankAccount || "";

        let disbursementId = FlowRouter.query.get('disbursementId');
        if (disbursementId) {
            this.params.disbursementId = disbursementId;
            this.handleRun();
        }
    },
    methods: {

        fetchBranch() {
            Meteor.call("queryRoleBranchOptionReport", (err, result) => {
                if (result) {
                    this.branchOptions = result;
                }
            })
        },
        fetchArea(branchList) {
            Meteor.call("queryRoleAreaOptionReport", branchList, (err, result) => {
                if (result) {
                    this.areaOptions = result;
                }
            })
        },
        clientOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    let lists = [];
                    Meteor.call('queryLoanDisbursementOption', query, Session.get("area"), (err, result) => {
                        if (!err) {
                            this.clientOptions = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryLoanDisbursementOption', "", Session.get("area"), (err, result) => {
                    if (!err) {
                        this.clientOptions = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        handleRun() {
            if (this.params.disbursementId === "") {
                this.$message({
                    duration: 1000,
                    message: "សូមមេត្តាជ្រើសរើស អតិថិជន",
                    type: 'error'
                });
                return false;
            }
            this.loading = true;

            Meteor.call('loanRepaymentScheduleReport', this.params, this.langConfig, (err, result) => {
                if (result) {
                    this.repaymentScheduleHtml = result.repaymentScheduleHtml;
                    this.dateHeader = result.dateHeader;
                    this.currencyHeader = result.currencyHeader;
                    this.currencySymbol = result.currencySymbol;

                    this.disbursementDoc = result.disbursementDoc;
                    this.clientDoc = result.clientDoc;
                    this.productDoc = result.productDoc;
                    this.creditOfficerDoc = result.creditOfficerDoc;
                }
                this.loading = false;
            });

        }
    },
    computed: {
        dataExist() {
            // return this.loanSaleData.length > 0;
        },
        langConfig() {
            let data = compoLangReport.filter(config => config.lang === this.langSessionReport)[0]['repaymentSchedule'];
            return data;
        }
    },
    components: {
        a4: PageA4
    },
}
