import compoLang from '../../../both/i18n/lang/elem-label-loan'
import {Loan_DisbursementReact} from "../../collection/loanDisbursement";
import {WB_waterBillingSetup} from "../../collection/waterBillingSetup";

export default {
    meteor: {
        langSession() {
            return Session.get('lang') || "en";
        },
        disabledRemove() {
            return Session.get("canRemove");
        },
        disabledUpdate() {
            return Session.get("canUpdate");
        },
        newRe() {
            let vm = this;
            Loan_DisbursementReact.find({}).fetch();
            vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
        }
    },
    mounted() {
        this.$jQuery('body').off();
        this.$jQuery('body').on('focus', 'input[type=number]', function(e) {
            $(this).on('wheel', function(e) {
                e.preventDefault();
            });
        });
    },
    data() {
        return {
            newLoading: true,
            langSession: null,
            loanDisbursementData: [],
            loading: false,
            searchData: '',
            isSearching: false,
            currentPage: 1,
            currentSize: 10,
            count: 0,
            dialogAddLoanDisbursement: false,
            dialogUpdateLoanDisbursement: false,
            isOperationFee: false,
            symbolFee: "",
            symbolOperationFee: "",

            loanDisbursementForm: {
                clientId: "",
                productId: "",
                currencyId: "",
                loanAmount: 0,
                projectInterest: "",
                disbursementDate: "",
                coId: "",
                status: "Active",
                description: "",
                startPaidDate: "",
                term: 0,
                repaidFrequency: 1,
                repaidPrinciple: 1,
                feeAmount: 0,
                feeType: "Amount",
                feeValue: 0,
                operationFeeAmount: 0,
                operationFeeType: "Amount",
                operationFeeOf: "Loan Amount",
                operationFeeValue: 0,
                guarantorId: "",
                purposeId: "",
                _id: ""
            },
            rules: {
                clientId: [{
                    required: true,
                    type: 'string',
                    message: 'Please choose Client',
                    trigger: 'change'
                }],
                productId: [{
                    required: true,
                    type: 'string',
                    message: 'Please choose Product',
                    trigger: 'change'
                }],
                currencyId: [{
                    required: true,
                    type: 'string',
                    message: 'Please choose Currency',
                    trigger: 'change'
                }],
                coId: [{
                    required: true,
                    type: 'string',
                    message: 'Please choose credit officer',
                    trigger: 'change'
                }],
                repaidFrequency: [{
                    required: true,
                    type: 'number',
                    message: 'Please choose Repaid Frequency',
                    trigger: 'change'
                }],
                repaidPrinciple: [{
                    required: true,
                    type: 'number',
                    message: 'Please choose Repaid Principle',
                    trigger: 'change'
                }],
                productType: [{
                    required: true,
                    type: 'string',
                    message: 'Please choose Product Type',
                    trigger: 'change'
                }],
                loanAmount: [{
                    required: true,
                    message: 'Please input Amount',
                    trigger: 'blur'
                }],
                feeValue: [{
                    required: true,
                    message: 'Please input Fee Value',
                    trigger: 'blur'
                }],
                operationFeeValue: [{
                    required: true,
                    message: 'Please input operation Fee Value',
                    trigger: 'blur'
                }],
                term: [{
                    required: true,
                    message: 'Please input term',
                    trigger: 'blur'
                }],
                disbursementDate: [{
                    type: 'date',
                    required: true,
                    message: 'Please input Disbursement Date',
                    trigger: 'blur'
                }],
                startPaidDate: [{
                    type: 'date',
                    required: true,
                    message: 'Please input Start Paid Date',
                    trigger: 'blur'
                }],
            },
            clientOption: [],
            guarantorOption: [],
            repaidFrequencyOption: [
                {value: 1, label: 1}
            ],
            repaidPrincipleOption: [
                {value: 1, label: 1}
            ],
            feeTypeOption: [
                {value: "Amount", label: "Amount"},
                {value: "Percent", label: "Percent"}

            ],
            operationFeeOfOption: [
                {value: "Loan Amount", label: "Loan Amount"},
                {value: "Loan Outstanding", label: "Loan Outstanding"},
                {value: "Principle", label: "Principle"}

            ],
            currencyOption: [
                {label: "USD", value: "USD"},
                {label: "KHR", value: "KHR"},
                {label: "THB", value: "THB"},
            ],
            productTypeOption: [

                {label: "Daily", value: "Daily"},
                {label: "Weekly", value: "Weekly"},
                {label: "BiWeekly", value: "BiWeekly"},
                {label: "Monthly", value: "Monthly"},
                {label: "Yearly", value: "Yearly"},
            ],
            productOption: [],
            purposeOption: [],
            creditOfficerOption: [],
            skip: 0,
            time: ""
        }
    },
    watch: {
        currentSize(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * val;
            this.queryData(this.searchData, this.skip, val + this.skip);
        },
        currentPage(val) {
            this.isSearching = true;
            this.skip = (val - 1) * this.currentSize;
            this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
        },
        searchData(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * this.currentSize;
            this.queryData(val, this.skip, this.currentSize + this.skip);
        },
        "loanDisbursementForm.term"(val) {
            if (val && val > 1) {
                this.repaidFrequencyOption = [];
                for (let i = 1; i < val / 2; i++) {
                    if (val % i === 0) {
                        this.repaidFrequencyOption.push({
                            label: i, value: i
                        })


                    }
                }
                if (this.loanDisbursementForm.repaidFrequency === 1) {
                    this.repaidPrincipleOption = [];
                    for (let i = 1; i < val / 2; i++) {
                        if (val % i === 0) {
                            this.repaidPrincipleOption.push({
                                label: i, value: i
                            })


                        }
                    }
                }
            } else {
                this.repaidFrequencyOption = [
                    {value: 1, label: 1}
                ];
            }
        },
        "loanDisbursementForm.repaidFrequency"(val) {
            let vm = this;
            if (val && val > 0) {
                this.repaidPrincipleOption = [];
                for (let i = 1; i < (vm.loanDisbursementForm.term / val); i++) {
                    if ((vm.loanDisbursementForm.term / val) % i === 0) {
                        this.repaidPrincipleOption.push({
                            label: i, value: i
                        })
                    }
                }
            } else {
                this.repaidPrincipleOption = [
                    {value: 1, label: 1}
                ];
            }
        },
        "loanDisbursementForm.productId"(val) {
            if (val) {
                let values = this.productOption.map(function (o) {
                    return o.value;
                })
                let index = values.indexOf(val);
                this.time = this.productOption[index].label.split(":")[1].trim();
            }
        },
        "loanDisbursementForm.feeType"(val) {
            this.symbolFee = val === "Percent" ? "%" : "";
        },
        "loanDisbursementForm.operationFeeType"(val) {
            this.symbolOperationFee = val === "Percent" ? "%" : "";
        },
        dialogAddLoanDisbursement(val) {
            if (val) {
                this._inputMaskDisbursement();
            }
        },
        dialogUpdateLoanDisbursement(val) {
            if (val) {
                this._inputMaskDisbursement();
            }
        }

    },
    methods: {
        handleSizeChange(val) {
            this.currentSize = val;
        },
        handleCurrentChange(val) {
            this.currentPage = val;
        },
        queryData: _.debounce(function (val, skip, limit) {
            this.newLoading = true;
            Meteor.call('queryLoanDisbursement', {
                q: val,
                filter: this.filter,
                rolesArea: Session.get('area'),
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {
                    this.loanDisbursementData = result.content;
                    this.count = result.countLoanDisbursement;
                }
                this.isSearching = false;
                this.newLoading = false;
            });
        }, 300),

        _inputMaskDisbursement() {
            setTimeout(() => {
                if ($(".el-inputLoanAmount").length) {
                    new this.$_Cleave('.el-inputLoanAmount', {
                        numeral: true,
                        numeralThousandsGroupStyle: 'thousand'
                    });
                }
            }, 200)
        },
        customerOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    Meteor.call('queryPosCustomerOption', query, Session.get("area"), (err, result) => {
                        if (!err) {
                            this.clientOption = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryPosCustomerOption', "", Session.get("area"), (err, result) => {
                    if (!err) {
                        this.clientOption = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        guarantorOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    Meteor.call('queryLoanGuarantorOption', query, Session.get("area"), (err, result) => {
                        if (!err) {
                            this.guarantorOption = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryLoanGuarantorOption', "", Session.get("area"), (err, result) => {
                    if (!err) {
                        this.guarantorOption = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        purposeOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    Meteor.call('queryLoanPurposeOption', query, Session.get("area"), (err, result) => {
                        if (!err) {
                            this.purposeOption = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryLoanPurposeOption', "", Session.get("area"), (err, result) => {
                    if (!err) {
                        this.purposeOption = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        creditOfficerOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    Meteor.call('queryLoanCreditOfficerOption', query, Session.get("area"), (err, result) => {
                        if (!err) {
                            this.creditOfficerOption = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryLoanCreditOfficerOption', "", Session.get("area"), (err, result) => {
                    if (!err) {
                        this.creditOfficerOption = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        productOpt(query) {
            if (!!query) {
                setTimeout(() => {
                    Meteor.call('queryLoanProductOption', query, (err, result) => {
                        if (!err) {
                            this.productOption = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }, 200);
            } else {
                Meteor.call('queryLoanProductOption', "", (err, result) => {
                    if (!err) {
                        this.productOption = result;
                    } else {
                        console.log(err.message);
                    }
                })
            }
        },
        goToSchedule(disbursementId){
            FlowRouter.go('/loan-report/loanRepaymentScheduleReport?disbursementId=' + disbursementId);
        },
        saveLoanDisbursement(event) {
            event.preventDefault();
            let vm = this;
            this.$refs["loanDisbursementFormAdd"].validate((valid) => {
                if (valid) {

                    let loanDisbursementDoc = {
                        clientId: vm.loanDisbursementForm.clientId,
                        guarantorId: vm.loanDisbursementForm.guarantorId,
                        productId: vm.loanDisbursementForm.productId,
                        purposeId: vm.loanDisbursementForm.purposeId,
                        currencyId: vm.loanDisbursementForm.currencyId,
                        loanAmount: vm.$_numeral(vm.loanDisbursementForm.loanAmount).value(),
                        disbursementDate: moment(vm.loanDisbursementForm.disbursementDate).startOf("days").add(12, "hours").toDate(),
                        disbursementDateName: moment(vm.loanDisbursementForm.disbursementDate).format("DD/MM/YYYY"),
                        coId: vm.loanDisbursementForm.coId,
                        term: vm.loanDisbursementForm.term,
                        repaidFrequency: vm.loanDisbursementForm.repaidFrequency,
                        repaidPrinciple: vm.loanDisbursementForm.repaidPrinciple,
                        description: vm.loanDisbursementForm.description,
                        feeAmount: vm.loanDisbursementForm.feeType === "Percent" ? vm.$_numeral(vm.loanDisbursementForm.loanAmount).value() * (vm.loanDisbursementForm.feeValue / 100) : vm.loanDisbursementForm.feeValue,
                        feeType: vm.loanDisbursementForm.feeType,
                        feeValue: vm.loanDisbursementForm.feeValue,

                        operationFeeAmount: vm.loanDisbursementForm.operationFeeType === "Percent" ? vm.$_numeral(vm.loanDisbursementForm.loanAmount).value() * (vm.loanDisbursementForm.operationFeeValue / 100) : vm.loanDisbursementForm.operationFeeValue,
                        operationFeeType: vm.loanDisbursementForm.operationFeeType,
                        operationFeeValue: vm.loanDisbursementForm.operationFeeValue,
                        operationFeeOf: vm.loanDisbursementForm.operationFeeOf,
                        startPaidDate: moment(vm.loanDisbursementForm.startPaidDate).startOf("days").add(12, "hours").toDate(),
                        rolesArea: Session.get('area')

                    };

                    Meteor.call("insertLoanDisbursement", loanDisbursementDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['saveSuccess'],
                                type: 'success'
                            });
                            vm.dialogAddLoanDisbursement = false;
                            vm.resetForm();

                        } else {
                            vm.$message({
                                duration: 1000,
                                message: err.message,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        updateLoanDisbursement() {
            let vm = this;
            this.$refs["loanDisbursementFormUpdate"].validate((valid) => {
                if (valid) {
                    let loanDisbursementDoc = {
                        _id: vm.loanDisbursementForm._id,
                        clientId: vm.loanDisbursementForm.clientId,
                        guarantorId: vm.loanDisbursementForm.guarantorId,
                        productId: vm.loanDisbursementForm.productId,
                        purposeId: vm.loanDisbursementForm.purposeId,
                        currencyId: vm.loanDisbursementForm.currencyId,
                        loanAmount: vm.$_numeral(vm.loanDisbursementForm.loanAmount).value(),
                        disbursementDate: moment(vm.loanDisbursementForm.disbursementDate).startOf("days").add(12, "hours").toDate(),
                        disbursementDateName: moment(vm.loanDisbursementForm.disbursementDate).format("DD/MM/YYYY"),
                        coId: vm.loanDisbursementForm.coId,
                        term: vm.loanDisbursementForm.term,
                        repaidFrequency: vm.loanDisbursementForm.repaidFrequency,
                        repaidPrinciple: vm.loanDisbursementForm.repaidPrinciple,
                        description: vm.loanDisbursementForm.description,
                        feeAmount: vm.loanDisbursementForm.feeType === "Percent" ? vm.$_numeral(vm.loanDisbursementForm.loanAmount).value() * (vm.loanDisbursementForm.feeValue / 100) : vm.loanDisbursementForm.feeValue,
                        feeType: vm.loanDisbursementForm.feeType,
                        feeValue: vm.loanDisbursementForm.feeValue,

                        operationFeeAmount: vm.loanDisbursementForm.operationFeeType === "Percent" ? vm.$_numeral(vm.loanDisbursementForm.loanAmount).value() * (vm.loanDisbursementForm.operationFeeValue / 100) : vm.loanDisbursementForm.operationFeeValue,
                        operationFeeType: vm.loanDisbursementForm.operationFeeType,
                        operationFeeValue: vm.loanDisbursementForm.operationFeeValue,
                        operationFeeOf: vm.loanDisbursementForm.operationFeeOf,
                        startPaidDate: moment(vm.loanDisbursementForm.startPaidDate).startOf("days").add(12, "hours").toDate(),
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("updateLoanDisbursement", loanDisbursementDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateSuccess'],
                                type: 'success'
                            });
                            vm.dialogUpdateLoanDisbursement = false;

                            vm.resetForm();

                        } else {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateFail'],
                                type: 'error'
                            });
                        }

                    })
                }
            })

        },
        removeLoanDisbursement(index, row, rows) {
            if (row.paymentNumber > 0) {
                this.$message({
                    type: 'error',
                    message: "can't remove, already have repayment !"
                });
                return false;
            }

            let vm = this;
            this.$confirm(this.langConfig['message'], this.langConfig['warning'], {
                confirmButtonText: 'OK',
                cancelButtonText: 'Cancel',
                type: 'warning'
            }).then(() => {
                Meteor.call("removeLoanDisbursement", row._id, (err, result) => {
                    if (!err) {
                        rows.splice(index, 1);

                        vm.$message({
                            message: this.langConfig['removeSuccess'],
                            type: 'success'
                        });


                    } else {
                        vm.$message({
                            type: 'error',
                            message: this.langConfig['removeFail']
                        });
                    }

                })

            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: this.langConfig['cancel']
                });
            });
        },
        findLoanDisbursementById(doc) {
            let vm = this;
            vm.loanDisbursementForm = {};

            Meteor.call("queryLoanDisbursementById", doc.row._id, (err, result) => {
                if (result) {
                    vm.loanDisbursementForm._id = result._id;
                    vm.loanDisbursementForm = result;
                }
            })
        },
        popUpUpdate(row) {
            if (row.paymentNumber > 0) {
                this.$message({
                    type: 'error',
                    message: "can't update, already have repayment !"
                });
                return false;
            }
            this.dialogUpdateLoanDisbursement = true;

        },
        cancel() {
            this.$message({
                type: 'info',
                message: this.langConfig['cancel']
            });
        },
        resetForm() {
            this.loanDisbursementForm._id = "";
            if (this.$refs["loanDisbursementFormAdd"]) {
                this.$refs["loanDisbursementFormAdd"].resetFields();
            }

            if (this.$refs["loanDisbursementFormUpdate"]) {
                this.$refs["loanDisbursementFormUpdate"].resetFields();
            }
            this.time = "";
            this.purposeOpt();

        },
        updateDateWithAdd12Hour() {
            Meteor.call("queryLoanDisbursementUpdateDate", (err, result) => {
                let dataResult = [];
                result.forEach((obj) => {
                    obj.disbursementDate = moment(obj.disbursementDate).startOf("day").add(12, "hours").toDate();
                    obj.disbursementDateName = moment(obj.disbursementDate).format("DD/MM/YYYY");
                    dataResult.push(obj);
                })

                Meteor.call("updateLoanDisbursementUpdateDate", dataResult, (err, result) => {

                })
            })

            Meteor.call("queryLoanRepaymentUpdateDate", (err, result) => {
                let dataResult = [];
                result.forEach((obj) => {
                    obj.repaymentDate = moment(obj.repaymentDate).startOf("day").add(12, "hours").toDate();
                    obj.repaymentDateName = moment(obj.repaymentDate).format("DD/MM/YYYY");
                    dataResult.push(obj);
                })

                Meteor.call("updateLoanRepaymentUpdateDate", dataResult, (err, result) => {

                })
            })
            Meteor.call("queryLoanRepaymentScheduleUpdateDate", (err, result) => {
                let dataResult = [];
                result.forEach((obj) => {
                    obj.date = moment(obj.date).startOf("day").add(12, "hours").toDate();
                    obj.dateName = moment(obj.date).format("DD/MM/YYYY");
                    dataResult.push(obj);
                })

                Meteor.call("updateLoanRepaymentScheduleUpdateDate", dataResult, (err, result) => {

                })
            })
        }
    },
    created() {
        this.isSearching = true;
        this.queryData();
        this.customerOpt();
        this.guarantorOpt();
        this.productOpt();
        this.purposeOpt();
        this.creditOfficerOpt();
        Meteor.subscribe('Loan_DisbursementReact');

        let setupDoc = WB_waterBillingSetup.findOne({});
        this.isOperationFee = setupDoc.isOperationFee;

    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['disbursement'];
            return data;
        }
    }
}
