            this.isSearching = true;
            this.skip = (this.currentPage - 1) * this.currentSize;
            this.queryData(val, this.skip, this.currentSize + this.skip);
        },
        "posCustomerForm.provinceId"(val) {
            this.posDistrictOption(val);
            if (val != "") {
                this.isDisableAddress = true;
                let pro = this.provinceOption.find((item) => {
                    return item.value === val;
                })
                if (pro) {
                    this.provinceName = " ខេត្ត " + pro.label.split(" | ")[1];
                }
            } else {
                this.isDisableAddress = false;
                this.posCustomerForm.districtId = "";
                this.posCustomerForm.communeId = "";
                this.posCustomerForm.villageId = "";

                this.provinceName = "";
                this.districtName = "";
                this.communeName = "";
                this.villageName = "";
            }
            this.posCustomerForm.address = this.villageName + this.communeName + this.districtName + this.provinceName;

        },
        "posCustomerForm.districtId"(val) {
            this.posCommuneOption(val);
            if (val !== "") {
                let dis = this.districtOption.find((item) => {
                    return item.value === val;
                })
                if (dis) {
                    this.districtName = " ស្រុក " + dis.label.split(" | ")[1];
                }
            }
            else {
                this.posCustomerForm.communeId = "";
                this.posCustomerForm.villageId = "";

                this.districtName = "";
                this.communeName = "";
                this.villageName = "";
            }
            this.posCustomerForm.address = this.villageName + this.communeName + this.districtName + this.provinceName;

        },
        "posCustomerForm.communeId"(val) {
            this.posVillageOption(val);
            if (val !== "") {
                let com = this.communeOption.find((item) => {
                    return item.value === val;
                })
                if (com) {
                    this.communeName = " ឃុំ " + com.label.split(" | ")[1];
                }
            } else {
                this.posCustomerForm.villageId = "";

                this.communeName = "";
                this.villageName = "";
            }
            this.posCustomerForm.address = this.villageName + this.communeName + this.districtName + this.provinceName;

        },
        "posCustomerForm.villageId"(val) {
            if (val !== "") {
                let vil = this.villageOption.find((item) => {
                    return item.value === val;
                })
                if (vil) {
                    this.villageName = "ភូមិ " + vil.label.split(" | ")[1];
                }
            } else {
                this.villageName = "";
            }
            this.posCustomerForm.address = this.villageName + this.communeName + this.districtName + this.provinceName;

        }
    },
    methods: {
        handleSizeChange(val) {
            this.currentSize = val;
        },
        handleCurrentChange(val) {
            this.currentPage = val;
        },
        queryData: _.debounce(function (val, skip, limit) {
            this.newLoading = true;
            Meteor.call('queryPosCustomer', {
                q: val,
                filter: this.filter,
                rolesArea: Session.get('area'),
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {
                    this.posCustomerData = result.content;
                    this.count = result.countPosCustomer;
                }
                this.isSearching = false;
                this.newLoading = false;
            });
        }, 300),
        posTermOption() {
            let selector = {};
            Meteor.call('queryPosTermOption', selector, this.posCustomerForm._id, (err, result) => {
                this.termDataOption = result;
            })
        },
        posProvinceOption() {
            let vm = this;
            Meteor.call("queryProvinceOption", (err, result) => {
                if (result) {
                    vm.provinceOption = result;
                }
            })
        },
        posDistrictOption(provinceId) {
            let vm = this;
            Meteor.call("queryDistrictOption", provinceId, (err, result) => {
                if (result) {
                    vm.districtOption = result;
                }
            })
        },
        posCommuneOption(districtId) {
            let vm = this;
            Meteor.call("queryCommuneOption", districtId, (err, result) => {
                if (result) {
                    vm.communeOption = result;
                }
            })
        },
        posVillageOption(communeId) {
            let vm = this;
            Meteor.call("queryVillageOption", communeId, (err, result) => {
                if (result) {
                    vm.villageOption = result;
                }
            })
        },
        savePosCustomer(event) {
            event.preventDefault();

            let vm = this;
            this.$refs["posCustomerFormAdd"].validate((valid) => {
                if (valid) {
                    let posCustomerDoc = {
                        name: vm.posCustomerForm.name,
                        khName: vm.posCustomerForm.khName,
                        address: vm.posCustomerForm.address,
                        email: vm.posCustomerForm.email,
                        phoneNumber: vm.posCustomerForm.phoneNumber,
                        termId: vm.posCustomerForm.termId,
                        openingBalance: vm.posCustomerForm.openingBalance,
                        note: vm.posCustomerForm.note,
                        dob: vm.posCustomerForm.dob,
                        nationId: vm.posCustomerForm.nationId,
                        type: vm.posCustomerForm.type,
                        homeNumber: vm.posCustomerForm.homeNumber,
                        groupNumber: vm.posCustomerForm.groupNumber,
                        provinceId: vm.posCustomerForm.provinceId,
                        districtId: vm.posCustomerForm.districtId,
                        communeId: vm.posCustomerForm.communeId,
                        villageId: vm.posCustomerForm.villageId,
                        provinceName: vm.provinceName,
                        districtName: vm.districtName,
                        communeName: vm.communeName,
                        villageName: vm.villageName,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("insertPosCustomer", posCustomerDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['saveSuccess'],
                                type: 'success'
                            });
                            vm.dialogAddPosCustomer = false;

                            vm.$refs["posCustomerFormAdd"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: err.message,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        updatePosCustomer() {
            let vm = this;
            this.$refs["posCustomerFormUpdate"].validate((valid) => {
                if (valid) {
                    let posCustomerDoc = {
                        _id: vm.posCustomerForm._id,
                        name: vm.posCustomerForm.name,
                        khName: vm.posCustomerForm.khName,
                        address: vm.posCustomerForm.address,
                        email: vm.posCustomerForm.email,
                        phoneNumber: vm.posCustomerForm.phoneNumber,
                        termId: vm.posCustomerForm.termId,
                        openingBalance: vm.posCustomerForm.openingBalance,
                        note: vm.posCustomerForm.note,
                        dob: vm.posCustomerForm.dob,
                        nationId: vm.posCustomerForm.nationId,
                        type: vm.posCustomerForm.type,
                        homeNumber: vm.posCustomerForm.homeNumber,
                        groupNumber: vm.posCustomerForm.groupNumber,
                        provinceId: vm.posCustomerForm.provinceId,
                        districtId: vm.posCustomerForm.districtId,
                        communeId: vm.posCustomerForm.communeId,
                        villageId: vm.posCustomerForm.villageId,
                        provinceName: vm.provinceName,
                        districtName: vm.districtName,
                        communeName: vm.communeName,
                        villageName: vm.villageName,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("updatePosCustomer", posCustomerDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateSuccess'],
                                type: 'success'
                            });
                            vm.dialogUpdatePosCustomer = false;

                            vm.$refs["posCustomerFormUpdate"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateFail'],
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        removePosCustomer(index, row, rows) {
            let vm = this;
            if (row.invoiceNumber <= 0 && row.loanCycle <= 0) {
                this.$confirm(this.langConfig['message'], this.langConfig['warning'], {
                    confirmButtonText: 'OK',
                    cancelButtonText: 'Cancel',
                    type: 'warning'
                }).then(() => {
                    Meteor.call("removePosCustomer", row._id, (err, result) => {
                        if (!err) {
                            rows.splice(index, 1);

                            vm.$message({
                                message: `${row.name}` + this.langConfig['removeSuccess'],
                                type: 'success'
                            });


                        } else {
                            vm.$message({
                                type: 'error',
                                message: this.langConfig['removeFail']
                            });
                        }

                    })

                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: this.langConfig['cancel']
                    });
                });
            } else {
                this.$message({
                    type: 'error',
                    message: this.langConfig['messageInovice'] + " " + row.invoiceNumber + " and Loan Cycle " + row.loanCycle
                });
            }


        },
        findPosCustomerById(doc) {
            let vm = this;
            vm.posCustomerForm = {};

            Meteor.call("queryPosCustomerById", doc.row._id, (err, result) => {
                if (result) {

                    vm.provinceName = result.provinceName || "";
                    vm.districtName = (result.districtName || "") + " ";
                    vm.communeName = (result.communeName || "") + " ";
                    vm.villageName = (result.villageName || "") + " ";

                    vm.posCustomerForm._id = result._id;
                    vm.posCustomerForm = result;
                }
            })
        },
        cancel() {
            this.$message({
                type: 'info',
                message: this.langConfig['cancel']
            });
        },
        resetForm() {
            this.posCustomerForm._id = "";
            this.posCustomerForm.type = "Identity Card";

            this.provinceName = "";
            this.districtName = "";
            this.communeName = "";
            this.villageName = "";

            if (this.$refs["posCustomerFormAdd"]) {
                this.$refs["posCustomerFormAdd"].resetFields();
                this.isDisableAddress = false;
            }

            if (this.$refs["posCustomerFormUpdate"]) {
                this.$refs["posCustomerFormUpdate"].resetFields();
            }

        }
    },
    created() {
        this.isSearching = true;
        this.posTermOption();
        this.queryData();
        this.posProvinceOption();
        Meteor.subscribe('Pos_CustomerReact');

        let setupDoc = WB_waterBillingSetup.findOne({});
        this.isValidateCustomer = setupDoc.isValidateCustomer || false;
        this.isNotValidateCustomer = !(setupDoc.isValidateCustomer || false);


    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['customer'];
            return data;
        }
    }
}
