import compoLang from '../../../both/i18n/lang/elem-label-loan'
import {Loan_GuarantorReact} from "../../collection/loanGuarantor";

export default {
    meteor: {
        langSession() {
            return Session.get('lang') || "en";
        },
        disabledRemove() {
            return Session.get("canRemove");
        },
        disabledUpdate() {
            return Session.get("canUpdate");
        },
        newRe() {
            let vm = this;
            Loan_GuarantorReact.find({}).fetch();
            vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
        }
    },
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            newLoading: true,
            langSession: null,
            loanGuarantorData: [],
            loading: false,
            searchData: '',
            isSearching: false,
            currentPage: 1,
            currentSize: 10,
            count: 0,
            dialogAddLoanGuarantor: false,
            dialogUpdateLoanGuarantor: false,

            loanGuarantorForm: {
                name: "",
                khName: "",
                address: "",
                email: "",
                phoneNumber: "",
                note: "",
                dob: "",
                nationId: "",
                _id: ""
            },
            rules: {
                name: [{required: true, message: 'Please input name', trigger: 'blur'}],
                nationId: [{required: true, message: 'Please input Nation Id', trigger: 'blur'}],
                address: [{required: true, message: 'Please input address', trigger: 'blur'}],
                phoneNumber: [{required: true, message: 'Please input Phone Number', trigger: 'blur'}],
                dob: [{
                    type: 'date',
                    required: true,
                    message: 'Please input Date of Birth',
                    trigger: 'blur'
                }],
            },
            termDataOption: [],
            skip: 0
        }
    },
    watch: {
        currentSize(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * val;
            this.queryData(this.searchData, this.skip, val + this.skip);
        },
        currentPage(val) {
            this.isSearching = true;
            this.skip = (val - 1) * this.currentSize;
            this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
        },
        searchData(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * this.currentSize;
            this.queryData(val, this.skip, this.currentSize + this.skip);
        }
    },
    methods: {
        handleSizeChange(val) {
            this.currentSize = val;
        },
        handleCurrentChange(val) {
            this.currentPage = val;
        },
        queryData: _.debounce(function (val, skip, limit) {
            this.newLoading = true;
            Meteor.call('queryLoanGuarantor', {
                q: val,
                filter: this.filter,
                rolesArea: Session.get('area'),
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {
                    this.loanGuarantorData = result.content;
                    this.count = result.countLoanGuarantor;
                }
                this.isSearching = false;
                this.newLoading = false;
            });
        }, 300),

        saveLoanGuarantor(event) {
            event.preventDefault();

            let vm = this;
            this.$refs["loanGuarantorFormAdd"].validate((valid) => {
                if (valid) {
                    let loanGuarantorDoc = {
                        name: vm.loanGuarantorForm.name,
                        khName: vm.loanGuarantorForm.khName,
                        address: vm.loanGuarantorForm.address,
                        email: vm.loanGuarantorForm.email,
                        phoneNumber: vm.loanGuarantorForm.phoneNumber,
                        note: vm.loanGuarantorForm.note,
                        dob: vm.loanGuarantorForm.dob,
                        nationId: vm.loanGuarantorForm.nationId,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("insertLoanGuarantor", loanGuarantorDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['saveSuccess'],
                                type: 'success'
                            });
                            vm.dialogAddLoanGuarantor = false;

                            vm.$refs["loanGuarantorForm"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: err.message,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        updateLoanGuarantor() {
            let vm = this;
            this.$refs["loanGuarantorFormUpdate"].validate((valid) => {
                if (valid) {
                    let loanGuarantorDoc = {
                        _id: vm.loanGuarantorForm._id,
                        name: vm.loanGuarantorForm.name,
                        khName: vm.loanGuarantorForm.khName,
                        address: vm.loanGuarantorForm.address,
                        email: vm.loanGuarantorForm.email,
                        phoneNumber: vm.loanGuarantorForm.phoneNumber,
                        note: vm.loanGuarantorForm.note,
                        dob: vm.loanGuarantorForm.dob,
                        nationId: vm.loanGuarantorForm.nationId,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("updateLoanGuarantor", loanGuarantorDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateSuccess'],
                                type: 'success'
                            });
                            vm.dialogUpdateLoanGuarantor = false;

                            vm.$refs["loanGuarantorForm"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: this.langConfig['updateFail'],
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        removeLoanGuarantor(index, row, rows) {
            let vm = this;

            Meteor.call("removeLoanGuarantor", row._id, (err, result) => {
                if (!err) {
                    rows.splice(index, 1);

                    vm.$message({
                        message: `${row.name}` + this.langConfig['removeSuccess'],
                        type: 'success'
                    });


                } else {
                    vm.$message({
                        type: 'error',
                        message: this.langConfig['removeFail']
                    });
                }

            });


        },
        findLoanGuarantorById(doc) {
            let vm = this;
            vm.loanGuarantorForm = {};

            Meteor.call("queryLoanGuarantorById", doc.row._id, (err, result) => {
                if (result) {
                    vm.loanGuarantorForm._id = result._id;
                    vm.loanGuarantorForm = result;
                }
            })
        },
        cancel() {
            this.$message({
                type: 'info',
                message: this.langConfig['cancel']
            });
        },
        resetForm() {
            this.loanGuarantorForm._id = "";
            if (this.$refs["loanGuarantorFormAdd"]) {
                this.$refs["loanGuarantorFormAdd"].resetFields();
            }

            if (this.$refs["loanGuarantorFormUpdate"]) {
                this.$refs["loanGuarantorFormUpdate"].resetFields();
            }

        }
    },
    created() {
        this.isSearching = true;
        this.queryData();
        Meteor.subscribe('Loan_GuarantorReact');

    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['guarantor'];
            return data;
        }
    }
}
